choose.when:
WHERE b.writer = m.id

	AND title LIKE #{keyword}


	AND writer LIKE #{keyword}


	AND content LIKE #{keyword}

choose.otherwise:
WHERE b.writer = m.id
AND (writer LIKE #{keyword}
	 OR title LIKE #{keyword}
	 OR content LIKE #{keyword} )

mapper.update:
UPDATE tbl_board
	SET viewcnt = viewcnt + 1
WHERE bno = #{bno}

	UPDATE tbl_board
   	 	SET title = #{title},
        	content = #{content}
	WHERE bno = #{bno}

UPDATE tbl_board
SET re_step = re_step + 1
WHERE re_step > #{re_step} AND ref=#{ref}
